const evaluateRule = (transaction: Transaction, rule: Rule): boolean => {
  return rule.conditions.every(condition => {
    switch(condition.field) {
      case 'description':
        return evaluateTextCondition(transaction.description, condition.operator, condition.value as string);
      case 'amount':
        return evaluateNumberCondition(transaction.amount, condition.operator, condition.value as number | [number, number]);
      // ... other field evaluations
    }
  });
};

const applyRulesToTransaction = async (transaction: Transaction) => {
  const { data: rules } = await supabase
    .from('rules')
    .select('*')
    .order('order_index', { ascending: true });

  if (!rules) return;

  for (const rule of rules) {
    if (evaluateRule(transaction, rule)) {
      for (const labelId of rule.labels_to_apply) {
        await applyLabelToTransaction(transaction.id, labelId);
      }
    }
  }
};
```

