const calculateReturns = (valuations: AssetValuation[]) => {
  if (valuations.length < 2) return 0;

  const initialValue = valuations[0].value;
  const currentValue = valuations[valuations.length - 1].value;

  return ((currentValue - initialValue) / initialValue) * 100;
};

const getPortfolioAllocation = async () => {
  const { data, error } = await supabase
    .from('assets')
    .select('*');

  if (error) throw error;

  // Calculate allocation percentages
  const total = data.reduce((sum, asset) => sum + asset.current_value, 0);

  return data.map(asset => ({
    ...asset,
    allocation_percentage: (asset.current_value / total) * 100
  }));
};
```

