1. Create a new hooks directory if it doesn't exist and implement the authenticated database operations hook:

```typescript
// hooks/useAuthenticatedMutation.ts
import { useState } from 'react';
import { supabase } from '@/lib/supabase';
import { useAuth } from '@/hooks/useAuth'; // Assuming this exists to get current user

type MutationOptions = {
  onSuccess?: (data: any) => void;
  onError?: (error: any) => void;
  table: string;
  validateFn?: (data: any) => { valid: boolean; message?: string };
}

export function useAuthenticatedMutation(options: MutationOptions) {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<any>(null);
  const { user } = useAuth(); // Get current authenticated user

  // Validate user is authenticated
  const validateAuth = () => {
    if (!user || !user.id) {
      throw new Error('User must be authenticated to perform this operation');
    }
    return user.id;
  };

  // Insert operation with automatic user_id injection
  const insert = async (data: any) => {
    setIsLoading(true);
    setError(null);

    try {
      const userId = validateAuth();

      // Run validation if provided
      if (options.validateFn) {
        const validation = options.validateFn(data);
        if (!validation.valid) {
          throw new Error(validation.message || 'Validation failed');
        }
      }

      // Inject user_id automatically
      const dataWithUser = { ...data, user_id: userId };

      const { data: result, error } = await supabase
        .from(options.table)
        .insert(dataWithUser)
        .select();

      if (error) throw error;

      if (options.onSuccess) options.onSuccess(result);
      return result;
    } catch (err) {
      setError(err);
      if (options.onError) options.onError(err);
      throw err;
    } finally {
      setIsLoading(false);
    }
  };

  // Update operation with user_id validation
  const update = async (id: string | number, data: any) => {
    setIsLoading(true);
    setError(null);

    try {
      const userId = validateAuth();

      // Run validation if provided
      if (options.validateFn) {
        const validation = options.validateFn(data);
        if (!validation.valid) {
          throw new Error(validation.message || 'Validation failed');
        }
      }

      // First verify the record belongs to this user
      const { data: existingRecord, error: fetchError } = await supabase
        .from(options.table)
        .select('user_id')
        .eq('id', id)
        .single();

      if (fetchError) throw fetchError;

      if (!existingRecord || existingRecord.user_id !== userId) {
        throw new Error('Unauthorized: Record does not belong to current user');
      }

      // Perform the update
      const { data: result, error } = await supabase
        .from(options.table)
        .update(data)
        .eq('id', id)
        .eq('user_id', userId) // Extra security to ensure RLS works correctly
        .select();

      if (error) throw error;

      if (options.onSuccess) options.onSuccess(result);
      return result;
    } catch (err) {
      setError(err);
      if (options.onError) options.onError(err);
      throw err;
    } finally {
      setIsLoading(false);
    }
  };

  // Delete operation with user_id validation
  const remove = async (id: string | number) => {
    setIsLoading(true);
    setError(null);

    try {
      const userId = validateAuth();

      // First verify the record belongs to this user
      const { data: existingRecord, error: fetchError } = await supabase
        .from(options.table)
        .select('user_id')
        .eq('id', id)
        .single();

      if (fetchError) throw fetchError;

      if (!existingRecord || existingRecord.user_id !== userId) {
        throw new Error('Unauthorized: Record does not belong to current user');
      }

      // Perform the delete
      const { data: result, error } = await supabase
        .from(options.table)
        .delete()
        .eq('id', id)
        .eq('user_id', userId); // Extra security to ensure RLS works correctly

      if (error) throw error;

      if (options.onSuccess) options.onSuccess(result);
      return result;
    } catch (err) {
      setError(err);
      if (options.onError) options.onError(err);
      throw err;
    } finally {
      setIsLoading(false);
    }
  };

  return {
    insert,
    update,
    remove,
    isLoading,
    error
  };
}
```

