export function LabelForm({ label, onSuccess }) {
  const [name, setName] = useState(label?.name || '');
  const [recurring, setRecurring] = useState(label?.recurring || false);

  // Validation function
  const validateLabel = (data) => {
    if (!data.name || data.name.trim() === '') {
      return { valid: false, message: 'Label name is required' };
    }
    return { valid: true };
  };

  // Use the authenticated mutation hook
  const { insert, update, isLoading, error } = useAuthenticatedMutation({
    table: 'labels',
    validateFn: validateLabel,
    onSuccess: (data) => {
      if (onSuccess) onSuccess(data);
      setName('');
      setRecurring(false);
    }
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const labelData = { name, recurring };

    if (label?.id) {
      await update(label.id, labelData);
    } else {
      await insert(labelData);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Form fields */}
      <input 
        type="text" 
        value={name} 
        onChange={(e) => setName(e.target.value)} 
        placeholder="Label name" 
      />
      <label>
        <input 
          type="checkbox" 
          checked={recurring} 
          onChange={(e) => setRecurring(e.target.checked)} 
        />
        Recurring
      </label>

      {error && <div className="error">{error.message}</div>}

      <button type="submit" disabled={isLoading}>
        {isLoading ? 'Saving...' : (label?.id ? 'Update' : 'Create')}
      </button>
    </form>
  );
}
