import { useState, useEffect } from 'react';
import { supabase } from '@/lib/supabase';
import { useAuthenticatedMutation } from '@/hooks/useAuthenticatedMutation';

export function LabelSelector({ selectedLabels = [], onChange }) {
  const [labels, setLabels] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  // Use the authenticated mutation hook for transaction-label relationships
  const { insert, remove } = useAuthenticatedMutation({
    table: 'transaction_labels',
    onSuccess: () => {
      // Refresh labels or handle success
    }
  });

  // Fetch all available labels
  useEffect(() => {
    const fetchLabels = async () => {
      setIsLoading(true);
      // This query is read-only, so we don't need the authenticated mutation hook
      const { data, error } = await supabase
        .from('labels')
        .select('*')
        .order('name');

      if (!error && data) {
        setLabels(data);
      }
      setIsLoading(false);
    };

    fetchLabels();
  }, []);

  const handleLabelToggle = async (labelId, transactionId) => {
    const isSelected = selectedLabels.some(label => label.id === labelId);

    if (isSelected) {
      // Remove the label from the transaction
      await remove({ transaction_id: transactionId, label_id: labelId });
    } else {
      // Add the label to the transaction
      await insert({ transaction_id: transactionId, label_id: labelId });
    }

    // Update the UI through the onChange callback
    const updatedLabels = isSelected
      ? selectedLabels.filter(label => label.id !== labelId)
      : [...selectedLabels, labels.find(label => label.id === labelId)];

    if (onChange) onChange(updatedLabels);
  };

  return (
    <div className="label-selector">
      {isLoading ? (
        <p>Loading labels...</p>
      ) : (
        <div className="label-list">
          {labels.map(label => (
            <button
              key={label.id}
              className={`label-badge ${selectedLabels.some(l => l.id === label.id) ? 'selected' : ''}`}
              onClick={() => handleLabelToggle(label.id)}
            >
              {label.name}
            </button>
          ))}
        </div>
      )}
    </div>
  );
}
