   // src/lib/currency/conversion.ts
   export async function convertAmount(amount: number, fromCurrency: string, toCurrency: string): Promise<number> {
     const rate = await getConversionRate(fromCurrency, toCurrency);
     return amount * rate;
   }

   export async function getConversionRate(fromCurrency: string, toCurrency: string): Promise<number> {
     // Initially fetch from our database of static rates
     // Structure allows for future API integration
     const { data, error } = await supabase
       .from('currency_rates')
       .select('rate')
       .eq('from_currency', fromCurrency)
       .eq('to_currency', toCurrency)
       .order('effective_date', { ascending: false })
       .limit(1);

     if (error || !data.length) {
       // Try reverse rate if available
       const reverseResult = await supabase
         .from('currency_rates')
         .select('rate')
         .eq('from_currency', toCurrency)
         .eq('to_currency', fromCurrency)
         .order('effective_date', { ascending: false })
         .limit(1);

       if (reverseResult.error || !reverseResult.data.length) {
         throw new Error(`No conversion rate found for ${fromCurrency} to ${toCurrency}`);
       }

       return 1 / reverseResult.data[0].rate;
     }

     return data[0].rate;
   }
   ```

