   // src/hooks/useCurrency.ts
   export function useCurrency() {
     const [userPreferences, setUserPreferences] = useState<UserPreferences | null>(null);

     useEffect(() => {
       // Fetch user preferences
       const fetchPreferences = async () => {
         const { data } = await supabase
           .from('user_preferences')
           .select('*')
           .single();
         setUserPreferences(data);
       };

       fetchPreferences();
     }, []);

     const convert = useCallback(async (amount: number, fromCurrency: string, toCurrency?: string) => {
       const targetCurrency = toCurrency || userPreferences?.default_currency || 'ILS';
       return convertAmount(amount, fromCurrency, targetCurrency);
     }, [userPreferences]);

     const format = useCallback((amount: number, currencyCode: string) => {
       return formatCurrency(amount, currencyCode);
     }, []);

     return { convert, format, userPreferences };
   }
