   // src/app/api/import/route.ts
   export async function POST(req: Request) {
     const { transactions, defaultCurrency } = await req.json();
     const { data: userPrefs } = await supabase
       .from('user_preferences')
       .select('default_currency')
       .single();

     const baseCurrency = userPrefs?.default_currency || 'ILS';

     // Process each transaction with currency conversion
     const processedTransactions = await Promise.all(transactions.map(async (tx) => {
       const currency = tx.original_currency || defaultCurrency || baseCurrency;
       const convertedAmount = await convertAmount(tx.amount, currency, baseCurrency);

       return {
         ...tx,
         original_currency: currency,
         converted_amount: convertedAmount,
         base_currency: baseCurrency,
         status: 'pending'
       };
     }));

     // Insert into database
     const { data, error } = await supabase
       .from('transactions')
       .insert(processedTransactions);

     if (error) {
       return Response.json({ error: error.message }, { status: 400 });
     }

     return Response.json({ success: true, count: processedTransactions.length });
   }
