           .from('transactions')
           .select('*')
           .in('original_currency', selectedCurrencies)
           .lt('amount', 0) // Expenses are negative
           .eq('status', 'approved');

         if (error) {
           console.error('Error fetching data:', error);
           return;
         }

         setData(data);
       };

       fetchData();
     }, [selectedCurrencies]);

     // Process data for charts based on grouping preference
     const processedData = useMemo(() => {
       if (groupByCurrency) {
         // Group by currency first, then by category
         return Object.entries(groupBy(data, 'original_currency')).map(([currency, transactions]) => {
           const categoryData = Object.entries(groupBy(transactions, 'category')).map(([category, items]) => ({
             category,
             value: Math.abs(items.reduce((sum, tx) => sum + tx.amount, 0))
           }));

           return {
             currency,
             data: categoryData
           };
         });
       } else {
         // Convert all to base currency and group by category
         const baseCurrencyData = data.map(tx => ({
           ...tx,
           normalizedAmount: tx.converted_amount || tx.amount
         }));

         return Object.entries(groupBy(baseCurrencyData, 'category')).map(([category, items]) => ({
           category,
           value: Math.abs(items.reduce((sum, tx) => sum + tx.normalizedAmount, 0))
         }));
       }
     }, [data, groupByCurrency]);

     return (
       <div className="container py-8">
         <h1 className="text-2xl font-bold mb-6">Expense Analysis</h1>

         <div className="mb-6 space-y-4">
           <CurrencyFilter
             selectedCurrencies={selectedCurrencies}
             onChange={setSelectedCurrencies}
           />

           <div className="flex items-center space-x-2">
             <Switch
               id="group-by-currency"
               checked={groupByCurrency}
               onCheckedChange={setGroupByCurrency}
             />
             <Label htmlFor="group-by-currency">Group by currency</Label>
           </div>
         </div>

         {groupByCurrency ? (
           // Render multiple charts, one per currency
           processedData.map(({ currency, data }) => (
             <div key={currency} className="mb-8">
               <h2 className="text-xl font-semibold mb-4">{currency} Expenses</h2>
               <CategoryChart data={data} currency={currency} />
             </div>
           ))
         ) : (
           // Render single chart with all data converted to base currency
           <CategoryChart data={processedData} currency="ILS" />
         )}

         <CurrencyBreakdownChart
           data={Object.entries(groupBy(data, 'original_currency')).map(([currency, items]) => ({
             currency,
             value: Math.abs(items.reduce((sum, tx) => sum + tx.amount, 0))
           }))}
         />
       </div>
     );
   }
