   ```tsx
   // src/components/settings/ConversionRateEditor.tsx
   export function ConversionRateEditor() {
     const [isOpen, setIsOpen] = useState(false);
     const [form, setForm] = useState({
       from_currency: 'USD',
       to_currency: 'ILS',
       rate: '',
       effective_date: new Date().toISOString().split('T')[0]
     });

     const handleSubmit = async (e) => {
       e.preventDefault();

       const { error } = await supabase
         .from('currency_rates')
         .insert({
           ...form,
           rate: parseFloat(form.rate),
           effective_date: new Date(form.effective_date).toISOString()
         });

       if (error) {
         toast.error('Failed to add conversion rate');
       } else {
         toast.success('Conversion rate added successfully');
         setIsOpen(false);
         setForm({
           from_currency: 'USD',
           to_currency: 'ILS',
           rate: '',
           effective_date: new Date().toISOString().split('T')[0]
         });
       }
     };

     return (
       <>
         <Button onClick={() => setIsOpen(true)} variant="outline">
           Add Conversion Rate
         </Button>

         <Dialog open={isOpen} onOpenChange={setIsOpen}>
           <DialogContent>
             <DialogHeader>
               <DialogTitle>Add Conversion Rate</DialogTitle>
               <DialogDescription>
                 Add a new currency conversion rate that will be used for calculations
               </DialogDescription>
             </DialogHeader>

             <form onSubmit={handleSubmit} className="space-y-4">
               <div className="grid grid-cols-2 gap-4">
                 <div>
                   <Label htmlFor="from-currency">From Currency</Label>
                   <Select
                     id="from-currency"
                     value={form.from_currency}
                     onValueChange={(value) => setForm({...form, from_currency: value})}
                   >
                     <SelectTrigger>
                       <SelectValue />
                     </SelectTrigger>
                     <SelectContent>
                       <SelectItem value="USD">USD</SelectItem>
                       <SelectItem value="EUR">EUR</SelectItem>
                       <SelectItem value="GBP">GBP</SelectItem>
                       <SelectItem value="ILS">ILS</SelectItem>
                     </SelectContent>
                   </Select>
                 </div>

                 <div>
                   <Label htmlFor="to-currency">To Currency</Label>
                   <Select
                     id="to-currency"
                     value={form.to_currency}
                     onValueChange={(value) => setForm({...form, to_currency: value})}
                   >
                     <SelectTrigger>
                       <SelectValue />
                     </SelectTrigger>
                     <SelectContent>
                       <SelectItem value="ILS">ILS</SelectItem>
                       <SelectItem value="USD">USD</SelectItem>
                       <SelectItem value="EUR">EUR</SelectItem>
                       <SelectItem value="GBP">GBP</SelectItem>
                     </SelectContent>
                   </Select>
                 </div>
               </div>

               <div>
                 <Label htmlFor="rate">Conversion Rate</Label>
                 <Input
                   id="rate"
                   type="number"
                   step="0.0001"
                   value={form.rate}
                   onChange={(e) => setForm({...form, rate: e.target.value})}
                   required
                 />
                 <p className="text-sm text-muted-foreground mt-1">
                   1 {form.from_currency} = ? {form.to_currency}
                 </p>
               </div>

               <div>
                 <Label htmlFor="effective-date">Effective Date</Label>
                 <Input
                   id="effective-date"
                   type="date"
                   value={form.effective_date}
                   onChange={(e) => setForm({...form, effective_date: e.target.value})}
                   required
                 />
               </div>

               <DialogFooter>
                 <Button type="button" variant="outline" onClick={() => setIsOpen(false)}>
                   Cancel
                 </Button>
                 <Button type="submit">Add Rate</Button>
               </DialogFooter>
             </form>
           </DialogContent>
         </Dialog>
       </>
     );
   }
